import { toast, ToastOptions } from 'react-hot-toast';

const CustomToast = {
  success: (message: string, options?: ToastOptions) => {
    return toast(
      (t) => (
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px', minWidth: '200px' }}>
          <span>{message}</span>
          <button
            onClick={() => toast.dismiss(t.id)}
            style={{
              background: 'transparent',
              border: 'none',
              color: '#fff',
              cursor: 'pointer',
              padding: '4px',
              marginLeft: 'auto',
              fontSize: '16px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
            aria-label="Close"
          >
            ✕
          </button>
        </div>
      ),
      {
        ...options,
        style: {
          background: '#68D391',
          color: '#fff',
          ...options?.style,
        },
      }
    );
  },

  error: (message: string, options?: ToastOptions) => {
    return toast(
      (t) => (
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px', minWidth: '200px' }}>
          <span>{message}</span>
          <button
            onClick={() => toast.dismiss(t.id)}
            style={{
              background: 'transparent',
              border: 'none',
              color: '#fff',
              cursor: 'pointer',
              padding: '4px',
              marginLeft: 'auto',
              fontSize: '16px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
            aria-label="Close"
          >
            ✕
          </button>
        </div>
      ),
      {
        ...options,
        style: {
          background: '#F56565',
          color: '#fff',
          ...options?.style,
        },
      }
    );
  },

  dismiss: toast.dismiss
};

export default CustomToast;
