import { toast, Toast } from 'react-hot-toast';

const CustomToast = {
  success: (message: string) => {
    toast.success(message, {
      duration: 5000,
      position: 'top-right',
      style: {
        background: '#68D391',
        color: '#fff',
        padding: '16px',
      },
    });
  },

  error: (message: string) => {
    toast.error(message, {
      duration: 5000,
      position: 'top-right',
      style: {
        background: '#F56565',
        color: '#fff',
        padding: '16px',
      },
    });
  },

  dismiss: toast.dismiss
};

export default CustomToast;
