import { toast, ToastOptions } from 'react-hot-toast';
import '../styles/toast.css';

const CustomToast = {
  success: (message: string, options?: ToastOptions) => {
    return toast(
      (t) => (
        <div 
          onClick={() => toast.dismiss(t.id)}
          style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between',
            width: '100%',
            cursor: 'pointer'
          }}
        >
          <span>{message}</span>
          <button
            onClick={(e) => {
              e.stopPropagation();
              toast.dismiss(t.id);
            }}
            className="toast-close-button"
            aria-label="Close"
          >
            ✕
          </button>
        </div>
      ),
      {
        ...options,
        className: 'toast-clickable',
        style: {
          background: '#68D391',
          color: '#fff',
          padding: '12px 16px',
          minWidth: '300px',
          ...options?.style,
        },
      }
    );
  },

  error: (message: string, options?: ToastOptions) => {
    return toast(
      (t) => (
        <div 
          onClick={() => toast.dismiss(t.id)}
          style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between',
            width: '100%',
            cursor: 'pointer'
          }}
        >
          <span>{message}</span>
          <button
            onClick={(e) => {
              e.stopPropagation();
              toast.dismiss(t.id);
            }}
            className="toast-close-button"
            aria-label="Close"
          >
            ✕
          </button>
        </div>
      ),
      {
        ...options,
        className: 'toast-clickable',
        style: {
          background: '#F56565',
          color: '#fff',
          padding: '12px 16px',
          minWidth: '300px',
          ...options?.style,
        },
      }
    );
  },

  dismiss: toast.dismiss
};

export default CustomToast;
