import { toast, ToastOptions } from 'react-hot-toast';

interface ToastMessage {
  message: string;
  type?: 'success' | 'error';
}

const ToastComponent: React.FC<ToastMessage> = ({ message, type }) => (
  <div 
    style={{ 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'space-between',
      gap: '8px',
      minWidth: '200px'
    }}
  >
    <span>{message}</span>
    <button
      onClick={() => toast.dismiss()}
      style={{
        background: 'transparent',
        border: 'none',
        color: '#fff',
        cursor: 'pointer',
        padding: '4px',
        marginLeft: '8px',
        fontSize: '16px'
      }}
    >
      âœ•
    </button>
  </div>
);

const CustomToast = {
  success: (message: string, options?: ToastOptions) => {
    return toast.success(<ToastComponent message={message} type="success" />, options);
  },
  error: (message: string, options?: ToastOptions) => {
    return toast.error(<ToastComponent message={message} type="error" />, options);
  },
  dismiss: toast.dismiss
};

export default CustomToast;
